    private void BtnSalarioActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnSalarioActionPerformed
        String Nombre=TxtNombre.getText();
        int Horas=Integer.parseInt(TxtHoras.getText());
        double SalarioHora=Double.parseDouble(TxtSalario.getText());
        Empleado empleado=new Empleado(Nombre,Horas,SalarioHora);
        double Pago=empleado.CalcularSalarioMensual();
        if(Pago>450000.0){
            OutTxt.setText(Nombre+" "+String.valueOf(Pago));
        }
        else{
            OutTxt.setText(Nombre);
        }
    }//GEN-LAST:event_BtnSalarioActionPerformed

    private void BtnBorrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnBorrarActionPerformed
        OutTxt.setText("");
        TxtHoras.setText("");
        TxtNombre.setText("");
        TxtSalario.setText("");
    }//GEN-LAST:event_BtnBorrarActionPerformed

